import React, {useState} from 'react'
import logo from '../Components/public/image/maxresdefault.jpg';
import cse from '../Components/public/image/yt1.png';



const Banner = () =>  {
    const [edit, setEdit] = useState({
        isEdit: false,
        name:"Hi! I'm Amit kumar",
        study:"I have completed BE in computer science engineering bhopal mp",
        wish:"Creator Youtube"
    })

    // const handleEdit = () =>{
    //     setEdit({
    //         isEdit: !edit.isEdit,
    //     })
    // }
    const handleClick = () =>{
         setEdit({
             isEdit : !edit.isEdit,
             name: edit.name,
             study: edit.study,
             wish: edit.wish,
            })
    }

    const updateValue = () =>{
          setEdit({
              isEdit: false,
              name : this.refs.theInput.value,
              study : this.refs.theInput.value,
              wish : this.refs.theInput.value,

          })
    }
        const isEdit = edit.isEdit
        return (
            <section className="container-banner">
                {
                  isEdit ? <form><br/>
                            <input type="text" 
                              defaultValue={edit.name}
                              ref = "theInput"
                               />
                            <button onClick={updateValue}>Update</button><br /><br />
                            <input type="text" 
                              defaultValue={edit.study}
                              ref = "theInput"
                               />
                            <button onClick={updateValue}>Update</button><br /><br />
                            <input type="text" 
                              defaultValue={edit.wish}
                              ref = "theInput"
                               />
                            <button onClick={updateValue}>Update</button><br />
                          </form>
                   :<div>
                            <a href="#home"><img id="profilepic" src={logo}  width="170" height="170" alt="profilepic"/></a>
                            <div onClick={handleClick} ><h1>{edit.name}</h1></div>
                            <div onClick={handleClick}><h3>{edit.study}</h3></div>
                            <div onClick={handleClick}><p id="paragarph1"> {edit.wish}</p></div>
                            <a id="logo" href="https://www.contentstack.com/" ><img src={cse} width="30" height="30" alt="cselogo"/></a> <br />
                            {/* <button className="btn-outline-secondary edit-btn"
                            onClick={handleEdit}
                            >Edit</button> */}
                    </div> 
                }
            </section>
        )
    }

export default Banner
