import React, {useState} from 'react'
import logo from '../Components/public/image/maxresdefault.jpg';
import cse from '../Components/public/image/yt1.png';



const Banner = () =>  {
    const [edit, setEdit] = useState({
        isEdit: false,
    })

    const handleEdit = () =>{
        setEdit({
            isEdit: !edit.isEdit,
        })
    }
    const handleClick = () =>{
         setEdit({isEdit : !edit.isEdit})
    }
        const isEdit = edit.isEdit
        return (
            <section className="container-banner">
                {
                  isEdit ? <form>
                            <input type="text" />
                          </form>
                   :<div>
                            <a href="#home"><img id="profilepic" src={logo}  width="170" height="170" alt="profilepic"/></a>
                            <h1 onClick={handleClick}> Hi! I'm Amit kumar </h1>
                            <h3> I have completed BE in computer science engineering bhopal mp </h3>
                            <p id="paragarph1"> Creator Youtube</p>
                            <a id="logo" href="https://www.contentstack.com/" ><img src={cse} width="30" height="30" alt="cselogo"/></a> <br />
                            <button className="btn-outline-secondary edit-btn"
                            onClick={handleEdit}
                            >Edit</button>
                    </div> 
                }
            </section>
        )
    }

export default Banner
